import Iitem from "../models/IItems";
import axios from 'axios';

const baseUrl = process.env.REACT_APP_BASE_URL;

const getItems = async () => {
    const response = await axios.get<Iitem[]>( `${baseUrl}/items` );
    return response.data;
}

export {getItems};
